$(document).ready(function () {
	$('.content').children().addClass('_anim-items _anim-show _anim-no-hide');
	const animItems = document.querySelectorAll("._anim-items"); // Элементы которые нужно анимировать
if(animItems.length > 0){
	window.addEventListener('scroll', animOnScroll);
	/* Что происходит: Для каждого элемента который имеет класс '_anim-items' при достижении 1/4 его
	высоты, либо 1/4 высоты окна браузера(если высота объекта выше высоты окна браузера) ему 
	добавляется класс '_active'. Если мы недокрутили до елемента, либо перекрутили то у него класс
	'_active' убирается. */
	function animOnScroll() {
		for(let index = 0; index < animItems.length; index++){ 
			const animItem = animItems[index]; // Получаем каждый объект отдельно
			const animItemHeight = animItem.offsetHeight; // Получаем высоту объекта
			const animItemOffset = offset(animItem).top; // Позиция объекта относительно верха
			const animStart = 4; // Коэффициент резулирующий момент старта анимации

			// Настройка момента старта анимации
			let animItemPoint = window.innerHeight - animItemHeight / animStart;

			// Для случая когда анимированный объект выше по высоте чем окно браузера
			if(animItemHeight > window.innerHeight){
				animItemPoint = window.innerHeight - window.innerHeight / 4;
			}

			// Добавляем элементам класс при определенных условиях
			if((pageYOffset > animItemOffset - animItemPoint) && pageYOffset < (animItemOffset + animItemHeight)){
				animItem.classList.add('_active');
				if(animItem.classList.contains('services')){
					bar[0].animate(0.8);  // Number from 0.0 to 1.0
					bar[1].animate(0.75);  // Number from 0.0 to 1.0
					bar[2].animate(0.6);  // Number from 0.0 to 1.0
				}else if (animItem.classList.contains('team')){
					skils[0].animate(0.6);  // Number from 0.0 to 1.0
					skils[1].animate(0.7);  // Number from 0.0 to 1.0
					skils[2].animate(0.32);  // Number from 0.0 to 1.0
					skils[3].animate(0.92);  // Number from 0.0 to 1.0
					skils[4].animate(0.71);  // Number from 0.0 to 1.0
					skils[5].animate(0.12);  // Number from 0.0 to 1.0
					skils[6].animate(0.68);  // Number from 0.0 to 1.0
					skils[7].animate(0.54);  // Number from 0.0 to 1.0
					skils[8].animate(0.64);  // Number from 0.0 to 1.0
				}
			}else{ // Убрать класс нужно для повторной анимации объекта
				/* Наличие у объекта класса '_anim-no-hide' говорит о том что не нужно объект  
				повторно анимировать если опять на него проскролили */
				if(!animItem.classList.contains('_anim-no-hide')){
					animItem.classList.remove('_active');
				}
			}
		}
	}
	function offset(el) {// Корректно и кроссбраузерно выщитывает позиция объекта относительно верха
		const rect = el.getBoundingClientRect(),
			scrollLeft = window.pageXOffset || document.documentElement.scrollLeft,
			scrollTop = window.pageYOffset || document.documentElement.scrollTop;
		return {top: rect.top + scrollTop, left: rect.left + scrollLeft }
	}

	setTimeout(function() {	
		animOnScroll();
	}, 2300);
};
	// Превращаем картинки из img в background-image для родителя
	function ibg(){ // Копируем в свой js файл и не забываем про стили ниже их тоже копируем
		let ibg=document.querySelectorAll(".ibg");
		for (var i = 0; i < ibg.length; i++) {
			if(ibg[i].querySelector('img')){
				ibg[i].style.backgroundImage = 'url('+ibg[i].querySelector('img').getAttribute('src')+')';
			}
		}
	}
	ibg();
	
	// Для работы бургер - меню
	$(".header__burger").click(function(){
		$(".header__burger,.header__menu").toggleClass('active');
		$('body').toggleClass('lock');
	});

	// Для корректного отображения в IE
	$(window).resize(function(){
		mainblock();
	});

	function mainblock() { 
		var h = $(window).outerHeight();
		$('.mainblock').css('min-height', h);
	}
	mainblock();

	// Делаем параллакс эффект. Фоновая картинка прокручивается медленнее
	$(window).scroll(function(){
	 	var s = 0 - $(this).scrollTop() / 3;
	 	$('.mainblock__image').css('transform', 'translate3d(0, '+s+'px, 0)');
	 });

	// Плавный скролл к блоку .content
	$("a.goto").click(function(e){
		e.preventDefault();
		var id = $(this).attr('href');
		var top = $(id).offset().top;
		$('body,html').animate({scrollTop: top}, 1000);
	});

	// Создаем объекты диаграммы
	var bar = [];
	for(var i = 0; i < 3; i++){
		bar[i] = new ProgressBar.Circle('#skils-'+i, {
		  color: '#81868E',
		  // This has to be the same size as the maximum width to
		  // prevent clipping
		  strokeWidth: 4,
		  trailWidth: 3,
		  trailColor: '#047378',
		  easing: 'easeInOut',
		  duration: 1400,
		  text: {
		    autoStyleContainer: false
		  },
		  from: { color: '#DD4F43', width: 4 },
		  to: { color: '#19BD9A', width: 4 },
		  // Set default step function for all animate calls
		  step: function(state, circle) {
		    circle.path.setAttribute('stroke', state.color);
		    circle.path.setAttribute('stroke-width', state.width);

		    var value = Math.round(circle.value() * 100);
		    if (value === 0) {
		      circle.setText('');
		    } else {
		      circle.setText(value + "<span id='percent'>%<span>");
		    }
		  }
		});
		bar[i].text.style.fontFamily = '"OpenSans-Bold"';
		bar[i].text.style.fontSize = '25px';
	}

	// Делаем фильтрацию
	$(".filter__item").click(function(){
		$(".filter__item").removeClass('active');
		$(this).addClass('active');

		var i = $(this).data('filter');
		if(i == 1){
			$('.portfolio__column').show();
		}else{
			$('.portfolio__column').hide();
			$('.portfolio__column.f_'+i).show();
		}
	});

	// Плавно скрываем/показываем описание для портфолио
	$(".portfolio__column").hover(function(){
		$(this).find('.hoverInfo').fadeIn(200);
	},function() {
		$(this).find('.hoverInfo').fadeOut(200);
	});

	// Создаем объекты скила для блока .team
	var skils = [];
	for(var i = 0; i < 9; i++){
		skils[i] = new ProgressBar.Line('#skils-team-'+i, {
		  strokeWidth: 4,
		  easing: 'easeInOut',
		  duration: 1400,
		  color: '#19bd9a',
		  trailColor: '#e1e4e9',
		  trailWidth: 4,
		  svgStyle: {width: '100%', height: '100%',position: 'absolute',top: '0px'},
		  text: {
		    style: {
		      // Text color.
		      // Default: same as stroke color (options.color)
		      color: '#999',
		      position: 'absolute',
		      right: '0',
		      top: '-18px',
		      fontSize: '16px',
		      padding: 0,
		      margin: 0,
		      transform: null
		    },
		    autoStyleContainer: false,
		  },
		  from: {color: '#19bd9a'},
		  to: {color: '#19bd9a'},
		  step: (state, bar) => {
		    bar.setText(Math.round(bar.value() * 100) + ' %');
		  }
		});
	}

	// Создаем слайдер №1
	$('.slider').slick({
		arrows: false,
		dots: true,
		adaptiveHeight: true
	});

	// Создаем слайдер №2
	$('.slider-testimonial').slick({
		arrows: false,
		dots: true,
		adaptiveHeight: true
	});

	// Создаем слайдер №3
	$('.news-slider').slick({
		arrows: false,
		dots: true,
		appendDots:$('#container-dots'),
		adaptiveHeight: true,
		vertical: true, // Вертикальное положение слайдера
		verticalSwiping: true, // Вертикальный свайп
		responsive:[ // Позволяет задавать брэйкпоинты для слайдера
			{ 
				breakpoint: 768, // Указываем ширину экрана при которой срабатывает брэйкпоинт
				settings: {
					vertical: false, // Вертикальное положение слайдера
					verticalSwiping: false, // Вертикальный свайп
					appendDots: $('.news-slider')
				}
			}
		],
		mobileFirst: false
	});
	
	// Показать/скрыть информацию о тарифах
	$(".price__item").hover(function(){
		$(this).find('.price__label').slideToggle(300);
		$(this).find('.price__content_1').slideUp(300);
		$(this).find('.price__content_2').slideDown(300);
	},function(){
		$(this).find('.price__label').slideToggle(300);
		$(this).find('.price__content_1').slideDown(300);
		$(this).find('.price__content_2').slideUp(300);
	});

	// Подключаю google maps
	let map = new google.maps.Map(document.getElementById("map"), {
    	center: { lat: -34.397, lng: 150.644 },
    	zoom: 8,
  	});

  	// Нажимаю кнопку OK чтоб не мешала
  	setTimeout(function() {
  		$('.dismissButton').click();
  	}, 8000);

  	// Скрыть чёрный фон над картой maps
  	$(".maps__bg").click(function(e){
  		e.stopPropagation();
  		$(this).fadeOut(500);
  	});
});